import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Login.css';
import axios from 'axios';

const Login = () => {
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const handleLogin = async (e) => {
    e.preventDefault();

    try {
      // Send login request to your backend API
      const response = await axios.post('http://localhost:8085/users/find', {
        email,
        password,
      });

      // Assuming a successful login returns a token or success message
      if (response.data.success) {
        // Navigate to home page if login is successful
        navigate('/home');
      } else {
        // If login fails, show error message
        setErrorMessage('Invalid email or password. Please try again.');
      }
    } catch (error) {
      // Handle error (e.g., if the server is down or API endpoint fails)
      setErrorMessage('Invalid email or password. Please try again.');
    }
  };

  const goToSignup = () => {
    navigate('/signup');
  };

  // Optional: Implement a function for password recovery
  const handleForgotPassword = () => {
    // You can implement password recovery logic here or navigate to a separate page
    console.log('Password recovery functionality not implemented yet.');
  };

  return (
    <div className="login-container">
      <div className="login-box">
        <h2>SIGN IN</h2>
        <form onSubmit={handleLogin}>
          {errorMessage && <p className="error-message">{errorMessage}</p>}
          <div className="input-field">
            <i className="fa fa-user"></i>
            <input
              type="text"
              placeholder="Username"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>
          <div className="input-field">
            <i className="fa fa-lock"></i>
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
          <div className="options">
            <label>
              <input type="checkbox" /> Remember me
            </label>
            {/* Replace the anchor with a button for password recovery */}
            <button type="button" onClick={handleForgotPassword} className="forgot-password-button">
              Forgot your password?
            </button>
          </div>
          <button type="submit" className="login-button">LOGIN</button>
        </form>
        <button onClick={goToSignup} className="signup-button">SIGN UP</button>
      </div>
    </div>
  );
};

export default Login;
